mapper.update:
UPDATE RC_GROUP SET GR_MEMCNT =
	(SELECT COUNT (*) FROM RC_GROUPMEM WHERE GR_ID = #{gr_id})
	WHERE GR_ID = #{gr_id}

mapper.select:
SELECT count(MEM_ID) 
FROM RC_GROUPMEM 
WHERE MEM_ID = #{mem_id}
AND GR_ID = #{gr_id}

SELECT * FROM (SELECT ROWNUM RNUMBER, R.* FROM ( (SELECT ROWNUM RNUM, P.* FROM (SELECT BR.BR_UUID, BR.BR_TITLE, M.MEM_NAME, TO_CHAR(BR.BR_REGDATE, 'YYYY/MM/DD') BR_REGDATE, BR.BR_NOTICEYN FROM RC_GRBOARD BR, RC_MEMBER M WHERE GR_ID = #{gr_id} AND BR.MEM_ID = M.MEM_ID AND BR.BR_DELYN = 'N'
AND BR.BR_TITLE LIKE '%${br_title}%'
ORDER BY BR_REFER, BR_REGDATE DESC ) P ORDER BY RNUM DESC) R) ) WHERE RNUMBER BETWEEN #{paging.start} AND #{paging.last}
