mapper.update:
UPDATE RC_SCHEDULE SET SCH_TITLE = #{sch_title}, SCH_CONTENT = #{sch_content}, SCH_STARTDATE = TO_DATE(#{sch_startDate}, 'YYYYMMDD'),
SCH_ENDDATE = TO_DATE(#{sch_endDate}, 'YYYYMMDD')
WHERE SCH_SEQ = #{sch_seq}

mapper.select:
SELECT SCH_SEQ, TO_CHAR(SCH_STARTDATE,'YYYY-MM-DD HH:MI') SCH_STARTDATE, TO_CHAR(SCH_STARTDATE,'YYYY-MM-DD HH:MI') SCH_ENDDATE, SCH_TITLE, SCH_CONTENT
FROM RC_SCHEDULE
WHERE SCH_SEQ = #{sch_Seq}
