mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, P.* FROM ( SELECT SB.SBR_UUID, SB.SBR_TITLE, M.MEM_NAME, TO_CHAR(SB.SBR_REGDATE, 'YYYY/MM/DD')SBR_REGDATE FROM RC_SYSBOARD SB, RC_MEMBER M WHERE SB.MEM_ID = M.MEM_ID AND SB.SBR_NOTICEYN = 'Y' AND SB.SBR_DELYN = 'N'
AND SB.SBR_TITLE LIKE '%${sbr_title}%'
ORDER BY SBR_REFER DESC, SBR_STEP ) P ) WHERE RNUM BETWEEN #{paging.start} AND #{paging.last}

mapper.update:
UPDATE RC_SYSBOARD 
SET SBR_TITLE = #{sbr_title}, SBR_CONTENT = #{sbr_content}, SBR_SCRYN = #{sbr_scryn}, SBR_PW = #{sbr_pw}
WHERE SBR_UUID = #{sbr_uuid}

UPDATE RC_SYSATTACH
SET SATT_NAME = #{satt_name}, SATT_RNAME = #{satt_rname}, SATT_SIZE = #{satt_size}, SATT_PATH = #{satt_path}
WHERE SBR_UUID = #{sbr_uuid}
